import 'package:flutter/material.dart';import 'ProductScreen.dart';import 'ProductModel.dart';import 'CheckoutScreen.dart';class cow extends StatefulWidget {  @override  _cowState createState() => _cowState();}List<ProductModel> cart = [];int sum = 0;class _cowState extends State<cow> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar:AppBar(title:Text('Cow'),backgroundColor: Colors.teal,actions: <Widget>[        IconButton(icon:Icon(Icons.shopping_cart,color:Colors.pink.shade900,size:20.0), onPressed: () {          Navigator.push(context, MaterialPageRoute(builder: (context) => new  CheckoutScreen(cart,sum)));        },),],) ,      body:Stack(        children: <Widget>[          ProductScreen((selectedProduct){            setState(() {              cart.add(selectedProduct); //update              sum = 0;              cart.forEach((item){                sum = sum + item.price;              });            });          },(unSelectedProduct){setState(() {            cart.remove(unSelectedProduct);            //update            sum=sum-unSelectedProduct.price;          });          }          ),        ],      ),    );  }}